import React, { useCallback } from "react";
import {
  MDBEdgeHeader,
  MDBFreeBird,
  MDBContainer,
  MDBCol,
  MDBRow,
  MDBCardBody,
  MDBIcon,
  MDBCard,
  MDBCardTitle,
  MDBCardImage,
  MDBCardText,
  MDBAnimation,
  MDBNavLink
} from "mdbreact";
import "./HomePage.css";
import UploadInput from "../components/uploadInput";
import firebase from "firebase";
import readXlsxFile from "read-excel-file";
import CSVReader from "react-csv-reader";
import Papa from "papaparse";

class HomePage extends React.Component {
  state = {
    typeFile: "",
    nameNovedades: "",
    nameAusencias: "",
    fileNovedades: null,
    fileAusencias: null,
    uploadValue: 0,
    agente: {
      legajo: "",
      nombre: ""
    }
  };

  scrollToTop = () => window.scrollTo(0, 0);
  handleClick = e => {
    this.setState({
      typeFile: e.target.value
    });
    if (e.target.value === "Novedades") {
      let input = document.getElementById("fileNovedades");
      input.click();
    } else {
      let input = document.getElementById("fileAusencias");
      input.click();
    }
  };
  handleChange = e => {
    let nameFile = e.target.value.substr(12);
    if (this.state.typeFile === "Novedades") {
      this.setState({
        nameNovedades: nameFile,
        fileNovedades: e.target.files
      });
    } else {
      this.setState({
        nameAusencias: nameFile,
        fileAusencias: e.target.files
      });
    }
    // console.log(
    //   this.state.typeFile,
    //   +" Archivo:" + nameFile + ",  file: " + e.target.files
    // );
    this.leerArchivo(e.target.files[0], e);
    // this.subirArchivo(e.target.files[0]);
  };

  leerArchivo(file, e) {
    let reader = new FileReader();
    reader.readAsDataURL(file);
    // reader.onload = e => {
    //   console.warn("data", e.target.result);
    // };

    Papa.parse(e.target.files[0], {
      complete: function(results) {
        var rows = results.data;
        console.log(rows);
        for (var i = 0; i < rows.length; i++) {
          console.log(
            "Legajo:" +
              rows[i][11] +
              "\nNombre: " +
              rows[i][12] +
              "\nFecha inicio: " +
              rows[i][16] +
              "\nFecha final: " +
              rows[i][17] +
              "\nDias: " +
              rows[i][18]
          );
        }
      }
    });
    // readXlsxFile(e.target.files[0]).then(rows => {
    //   console.log(" XLSXROW:" + rows);
    //   console.log(
    //     "Legajo:" +
    //       rows[0][11] +
    //       "\nNombre: " +
    //       rows[0][12] +
    //       "\nFecha inicio: " +
    //       rows[0][16] +
    //       "\nFecha final: " +
    //       rows[0][17] +
    //       +"\nDías: " +
    //       rows[0][18]
    //   );
    // });
  }

  subirArchivo(file) {
    const storageRef = firebase.storage().ref(`/Archivos/${file.name}`);
    const task = storageRef.put(file);
    task.on(
      "state_changed",
      snapshot => {
        let percentage =
          (snapshot.bytesTransferred / snapshot.totalBytes) * 100;
        this.setState({
          uploadValue: percentage
        });
      },
      error => {
        console.log(error.message);
      },
      () => {
        this.setState({
          uploadValue: 100
        });
      }
    );
  }

  generarDescuentos() {
    // let reader = new FileReader();
    // reader.readAsDataURL(this.state.fileNovedades[0]);
    // reader.onload = e => {
    //   console.warn("data", e.target.result);
    // };
  }

  render() {
    return (
      <>
        <MDBEdgeHeader color="indigo darken-3" className="sectionPage" />
        <div className="mt-3 mb-5">
          <MDBFreeBird>
            <MDBRow>
              <MDBCol
                md="10"
                className="mx-auto float-none white z-depth-1 py-2 px-2"
              >
                <MDBCardBody className="text-center">
                  <h2 className="h2-responsive mb-4">
                    <strong className="font-weight-bold">
                      Subir archivos (.xlsx)
                    </strong>
                  </h2>
                  <MDBRow />
                  <MDBRow className="d-flex flex-row justify-content-center row">
                    <button
                      className="btn Ripple-parent btn-elegant"
                      onClick={this.handleClick}
                      value="Novedades"
                    >
                      Novedades
                    </button>
                    <input
                      id="fileNovedades"
                      type="file"
                      hidden
                      onChange={this.handleChange}
                    ></input>
                    <button
                      className="btn Ripple-parent btn-elegant"
                      onClick={this.handleClick}
                      value="Ausencias"
                    >
                      Ausencias
                    </button>
                    <input
                      id="fileAusencias"
                      type="file"
                      hidden
                      onChange={this.handleChange}
                    ></input>
                  </MDBRow>
                  <MDBRow className="d-flex flex-row justify-content-center row">
                    <p className="textFile">{this.state.nameNovedades}</p>
                    <p className="textFile">{this.state.nameAusencias}</p>
                  </MDBRow>
                  <button
                    className="btn Ripple-parent btn-success top20"
                    onClick={this.generarDescuentos}
                  >
                    Generar
                  </button>
                </MDBCardBody>
              </MDBCol>
            </MDBRow>
          </MDBFreeBird>
          <MDBContainer></MDBContainer>
        </div>
      </>
    );
  }
}

export default HomePage;
