import React from "react";
import {
  MDBEdgeHeader,
  MDBFreeBird,
  MDBContainer,
  MDBCol,
  MDBRow,
  MDBCardBody,
  MDBTable,
  TableBody,
  MDBTableHead
} from "mdbreact";
import "./ResultPage.css";
// import ReactExport from "react-export-excel";
import XLSX from "xlsx";
import { Redirect } from "react-router-dom";

// const ExcelFile = ReactExport.ExcelFile;
// const ExcelSheet = ReactExport.ExcelFile.ExcelSheet;
// const ExcelColumn = ReactExport.ExcelFile.ExcelColumn;

class ResultPage extends React.Component {
  state = {
    agentes: []
  };

  componentDidMount = () => {
    setTimeout(
      function() {
        this.setState({ agentes: this.props.location.state.agentes });
      }.bind(this),
      1
    );
  };

  descargarExcel = () => {
    var wb = XLSX.utils.book_new();
    XLSX.write(wb, this.props.agentes);
    //XLSX.writeFile(this.props.agentes, "ausencias - 153");
  };

  render() {
    // console.log(this.props.location.state.agentes);
    const data = {
      columns: [
        {
          label: "Legajo",
          field: "id"
        },
        {
          label: "Apellido y Nombre",
          field: "heading0"
        },
        {
          label: "Días a descuento",
          field: "heading1"
        }
      ]
    };

    return (
      <>
        {this.props.location.state.agentes === null && (
          <Redirect
            to={{
              pathname: "/"
            }}
          />
        )}
        <MDBEdgeHeader color="indigo darken-3" className="ResultHeader" />
        <div className="mt-3 mb-5">
          <MDBFreeBird>
            <MDBRow>
              <MDBCol
                md="10"
                className="mx-auto float-none white z-depth-1 py-2 px-2"
              >
                <MDBCardBody className="text-center">
                  <h2 className="h2-responsive mb-4">
                    <strong className="font-weight-bold">Resultados</strong>
                  </h2>
                  <MDBRow />
                  <MDBTable responsiveSm>
                    <MDBTableHead
                      columns={data.columns}
                      color="indigo"
                      textWhite
                    />
                    <TableBody>
                      {this.state.agentes.map((item, i) => {
                        return (
                          <tr key={i}>
                            <td className="text-center">{item.legajo}</td>
                            <td>{item.nombre}</td>
                            <td className="text-center">{item.diasdesc}</td>
                          </tr>
                        );
                      })}
                    </TableBody>
                  </MDBTable>
                  <button
                    className="btn Ripple-parent btn-indigo top20"
                    onClick={this.descargarExcel}
                  >
                    Descargar descuentos (.xlsx)
                  </button>
                  {/* <ExcelFile
                    filename="Ausencias - 153"
                    element={
                      <button className="btn Ripple-parent btn-indigo top20">
                        Descargar descuentos (.xlsx)
                      </button>
                    }
                  >
                    <ExcelSheet data={this.props.agentes} name="Employees">
                      <ExcelColumn value="legajo" />
                      <ExcelColumn value="0" />
                      <ExcelColumn value="diasdesc" />
                    </ExcelSheet>
                  </ExcelFile> */}
                </MDBCardBody>
              </MDBCol>
            </MDBRow>
          </MDBFreeBird>
          <MDBContainer></MDBContainer>
        </div>
      </>
    );
  }
}

export default ResultPage;
