import React, { useCallback } from "react";
import {
  MDBEdgeHeader,
  MDBFreeBird,
  MDBContainer,
  MDBCol,
  MDBRow,
  MDBCardBody,
  MDBIcon,
  MDBCard,
  MDBCardTitle,
  MDBCardImage,
  MDBCardText,
  MDBAnimation,
  MDBNavLink
} from "mdbreact";
import "./HomePage.css";
import UploadInput from "../components/uploadInput";
import firebase from "firebase";
import readXlsxFile from "read-excel-file";
import CSVReader from "react-csv-reader";
import Papa from "papaparse";
import ModalAgentes from "../components/ModalAgentes";

class HomePage extends React.Component {
  state = {
    typeFile: "",
    nameNovedades: "",
    nameAusencias: "",
    fileNovedades: null,
    fileAusencias: null,
    uploadValue: 0,
    agente: []
  };

  scrollToTop = () => window.scrollTo(0, 0);
  handleClick = e => {
    this.setState({
      typeFile: e.target.value
    });
    if (e.target.value === "Novedades") {
      let input = document.getElementById("fileNovedades");
      input.click();
    } else {
      let input = document.getElementById("fileAusencias");
      input.click();
    }
  };
  handleChange = e => {
    let nameFile = e.target.value.substr(12);
    if (this.state.typeFile === "Novedades") {
      this.setState({
        nameNovedades: nameFile,
        fileNovedades: e.target.files
      });
    } else {
      this.setState({
        nameAusencias: nameFile,
        fileAusencias: e.target.files
      });
    }
    // console.log(
    //   this.state.typeFile,
    //   +" Archivo:" + nameFile + ",  file: " + e.target.files
    // );
    this.leerArchivo(e.target.files[0], e);
    // this.subirArchivo(e.target.files[0]);
  };

  leerArchivo(file, e) {
    let reader = new FileReader();
    reader.readAsDataURL(file);
    // reader.onload = e => {
    //   console.warn("data", e.target.result);
    // };

    var agentes = [];
    Papa.parse(e.target.files[0], {
      complete: function(results) {
        var rows = results.data;
        var i = 0;
        var legajoAct = rows[i][11];
        // console.log("rows: " + rows.length);
        while (i < rows.length) {
          var nuevo = null;
          legajoAct = rows[i][11];
          while (legajoAct === rows[i][11]) {
            nuevo = JSON.parse(
              JSON.stringify({ legajo: rows[i][11], nombre: rows[i][12] })
            );
            if (i + 1 < rows.length) {
              i++;
            } else {
              i++;
              break;
            }
            // console.log("i: " + i);
          }
          // console.log(nuevo);
          agentes.push(nuevo);
          // console.log("agentes: " + agentes[0].legajo);
        }
      }
    });
    this.setState({
      agente: agentes
    });
    console.log(this.state.agente);
    this.mostrarTabla();
  }

  mostrarTabla() {}

  subirArchivo(file) {
    const storageRef = firebase.storage().ref(`/Archivos/${file.name}`);
    const task = storageRef.put(file);
    task.on(
      "state_changed",
      snapshot => {
        let percentage =
          (snapshot.bytesTransferred / snapshot.totalBytes) * 100;
        this.setState({
          uploadValue: percentage
        });
      },
      error => {
        console.log(error.message);
      },
      () => {
        this.setState({
          uploadValue: 100
        });
      }
    );
  }

  generarDescuentos() {
    // let reader = new FileReader();
    // reader.readAsDataURL(this.state.fileNovedades[0]);
    // reader.onload = e => {
    //   console.warn("data", e.target.result);
    // };
  }

  render() {
    return (
      <>
        <ModalAgentes title="Novedades" />
        <MDBEdgeHeader color="indigo darken-3" className="sectionPage" />
        <div className="mt-3 mb-5">
          <MDBFreeBird>
            <MDBRow>
              <MDBCol
                md="10"
                className="mx-auto float-none white z-depth-1 py-2 px-2"
              >
                <MDBCardBody className="text-center">
                  <h2 className="h2-responsive mb-4">
                    <strong className="font-weight-bold">
                      Subir archivos (.csv)
                    </strong>
                  </h2>
                  <MDBRow />
                  <MDBRow className="d-flex flex-row justify-content-center row">
                    <button
                      className="btn Ripple-parent btn-elegant"
                      onClick={this.handleClick}
                      value="Novedades"
                    >
                      Novedades
                    </button>
                    <input
                      id="fileNovedades"
                      type="file"
                      hidden
                      onChange={this.handleChange}
                    ></input>
                    <button
                      className="btn Ripple-parent btn-elegant"
                      onClick={this.handleClick}
                      value="Ausencias"
                    >
                      Ausencias
                    </button>
                    <input
                      id="fileAusencias"
                      type="file"
                      hidden
                      onChange={this.handleChange}
                    ></input>
                  </MDBRow>
                  <MDBRow className="d-flex flex-row justify-content-center row">
                    <p className="textFile">{this.state.nameNovedades}</p>
                    <p className="textFile">{this.state.nameAusencias}</p>
                  </MDBRow>
                  <button
                    className="btn Ripple-parent btn-success top20"
                    onClick={this.generarDescuentos}
                  >
                    Generar
                  </button>
                </MDBCardBody>
              </MDBCol>
            </MDBRow>
          </MDBFreeBird>
          <MDBContainer></MDBContainer>
        </div>
      </>
    );
  }
}

export default HomePage;
