import React, { useCallback } from "react";
import {
  MDBEdgeHeader,
  MDBFreeBird,
  MDBContainer,
  MDBCol,
  MDBRow,
  MDBCardBody,
  MDBCard,
  MDBTable,
  MDBTableBody,
  TableBody,
  MDBTableHead
} from "mdbreact";
import "./ResultPage.css";
import ReactExport from "react-export-excel";
const ExcelFile = ReactExport.ExcelFile;
const ExcelSheet = ReactExport.ExcelFile.ExcelSheet;
const ExcelColumn = ReactExport.ExcelFile.ExcelColumn;
class ResultPage extends React.Component {
  state = {};

  render() {
    const agentes = [];
    const data = {
      columns: [
        {
          label: "Legajo",
          field: "id"
        },
        {
          label: "Apellido y Nombre",
          field: "heading0"
        },
        {
          label: "Días a descuento",
          field: "heading1"
        }
      ]
    };

    return (
      <>
        <MDBEdgeHeader color="indigo darken-3" className="ResultHeader" />
        <div className="mt-3 mb-5">
          <MDBFreeBird>
            <MDBRow>
              <MDBCol
                md="10"
                className="mx-auto float-none white z-depth-1 py-2 px-2"
              >
                <MDBCardBody className="text-center">
                  <h2 className="h2-responsive mb-4">
                    <strong className="font-weight-bold">Resultados</strong>
                  </h2>
                  <MDBRow />

                  <MDBTable responsiveSm>
                    <MDBTableHead
                      columns={data.columns}
                      color="indigo"
                      textWhite
                    />
                    <TableBody>
                      {this.props.agentes.map((item, i) => {
                        if (item.diasdesc > 0) {
                          return (
                            <tr key={i}>
                              <td className="text-center">{item.legajo}</td>
                              <td>{item.nombre}</td>
                              <td className="text-center">{item.diasdesc}</td>
                            </tr>
                          );
                        }
                      })}
                    </TableBody>
                  </MDBTable>

                  <button className="btn Ripple-parent btn-indigo top20">
                    Descargar descuentos (.xlsx)
                  </button>
                  <ExcelFile element={<button>Download Data</button>}>
                    <ExcelSheet data={dataSet1} name="Employees">
                      <ExcelColumn label="Name" value="name" />
                      <ExcelColumn label="Wallet Money" value="amount" />
                      <ExcelColumn label="Gender" value="sex" />
                      <ExcelColumn
                        label="Marital Status"
                        value={col => (col.is_married ? "Married" : "Single")}
                      />
                    </ExcelSheet>
                    <ExcelSheet data={dataSet2} name="Leaves">
                      <ExcelColumn label="Name" value="name" />
                      <ExcelColumn label="Total Leaves" value="total" />
                      <ExcelColumn label="Remaining Leaves" value="remaining" />
                    </ExcelSheet>
                  </ExcelFile>
                </MDBCardBody>
              </MDBCol>
            </MDBRow>
          </MDBFreeBird>
          <MDBContainer></MDBContainer>
        </div>
      </>
    );
  }
}

export default ResultPage;
