import React from "react";
import {
  MDBEdgeHeader,
  MDBFreeBird,
  MDBContainer,
  MDBCol,
  MDBRow,
  MDBCardBody,
  MDBIcon,
  MDBCard,
  MDBCardTitle,
  MDBCardImage,
  MDBCardText,
  MDBAnimation,
  MDBNavLink
} from "mdbreact";
import "./HomePage.css";
import UploadInput from "../components/uploadInput";
import * as XLSX from "xlsx";

class HomePage extends React.Component {
  state = {
    typeFile: "",
    nameNovedades: "",
    nameAusencias: ""
    // fileNovedades: null,
    // fileAusencias: null
  };
  scrollToTop = () => window.scrollTo(0, 0);
  handleClick = e => {
    this.setState({
      typeFile: e.target.value
    });
    if (e.target.value == "Novedades") {
      let input = document.getElementById("fileNovedades");
      input.click();
    } else {
      let input = document.getElementById("fileAusencias");
      input.click();
    }
  };
  handleChange = e => {
    let nameFile = e.target.value.substr(12);
    if (this.state.typeFile == "Novedades") {
      this.setState({
        nameNovedades: nameFile
        // fileNovedades: e.target.files
      });
    } else {
      this.setState({
        nameAusencias: nameFile
        // fileAusencias: e.target.files
      });
    }
    console.log(
      this.state.typeFile,
      +" Archivo:" + nameFile + ",  file: " + e.target.files
    );
  };
  // generarDescuentos() {
  //   // read.XLSX();
  // }

  render() {
    return (
      <>
        <MDBEdgeHeader color="indigo darken-3" className="sectionPage" />
        <div className="mt-3 mb-5">
          <MDBFreeBird>
            <MDBRow>
              <MDBCol
                md="10"
                className="mx-auto float-none white z-depth-1 py-2 px-2"
              >
                <MDBCardBody className="text-center">
                  <h2 className="h2-responsive mb-4">
                    <strong className="font-weight-bold">
                      Subir archivos (.xlsx)
                    </strong>
                  </h2>
                  <MDBRow />
                  <form>
                    <MDBRow className="d-flex flex-row justify-content-center row">
                      <button
                        className="btn Ripple-parent btn-elegant"
                        onClick={this.handleClick}
                        value="Novedades"
                      >
                        Novedades
                      </button>
                      <input
                        id="fileNovedades"
                        type="file"
                        hidden
                        onChange={this.handleChange}
                      ></input>
                      <button
                        className="btn Ripple-parent btn-elegant"
                        onClick={this.handleClick}
                        value="Ausencias"
                      >
                        Ausencias
                      </button>
                      <input
                        id="fileAusencias"
                        type="file"
                        hidden
                        onChange={this.handleChange}
                      ></input>
                    </MDBRow>
                    <MDBRow className="d-flex flex-row justify-content-center row">
                      <p className="textFile">{this.state.nameNovedades}</p>
                      <p className="textFile">{this.state.nameAusencias}</p>
                    </MDBRow>
                    <button
                      className="btn Ripple-parent btn-success top20"
                      onClick={this.generarDescuentos}
                    >
                      Generar
                    </button>
                  </form>
                </MDBCardBody>
              </MDBCol>
            </MDBRow>
          </MDBFreeBird>
          <MDBContainer></MDBContainer>
        </div>
      </>
    );
  }
}

export default HomePage;
